import axios from 'axios';
export default {
  name: 'baseform',
  data: function data() {
    return {
      reservation: {
        studentId: '',
        coachId: '',
        startTime: '',
        endTime: '',
        schedule: '0',
        remarks: '',
        studentName: '',
        coachName: ''
      }
    };
  },
  created: function created() {
    this.getStudent();
  },
  methods: {
    onSubmit: function onSubmit() {
      console.log(this.reservation);

      var _this = this;

      this.$message.success('提交成功！');
      axios.post('/api/student/reservation', this.reservation).then(function (res) {
        var data = res.data;

        _this.$message.success(data.msg);

        _this.$router.push('/table');
      });
    },
    getStudent: function getStudent() {
      console.log(localStorage.getItem('ms_id'));
      var studentId = localStorage.getItem('ms_id');
      console.log(studentId);
      var path = '/api/student/student/pro/' + studentId;
      console.log(path);

      var _this = this;

      axios.get(path).then(function (res) {
        var data = res.data.data;
        _this.reservation.studentId = data.id;
        _this.reservation.studentName = data.username;
        _this.reservation.coachId = data.coachId;
        console.log(_this.reservation.studentName);
        console.log(_this.reservation.coachId);
        console.log("====================");

        _this.getCoach();
      }).catch(function (error) {
        console.log(error);
      });
    },
    getCoach: function getCoach() {
      var _this = this;

      console.log("cccc" + _this.reservation.coachId);
      var path = '/api/coach/coach/pro/' + _this.reservation.coachId;
      console.log(path);
      axios.get(path).then(function (res) {
        var data = res.data.data;
        _this.reservation.coachName = data.username;
        console.log(_this.reservation.coachName);
        console.log(_this.reservation.studentName);
      }).catch(function (error) {
        console.log(error);
      });
    }
  }
};
